const SwitchTrack = styled('span')(
  ({ theme }) => `
  background-color: ${
    theme.palette.mode === 'dark' ? 'rgba(107, 81, 24, 0.1)' : 'rgba(107, 81, 24, 0.1)'
  };
  border-radius: 10px;
  border: 1px gray solid;
  width: 100%;
  height: 100%;
  display: block;
`,
);

function SwitchTheme(props) {
  const { getInputProps /* , disabled, focusVisible  */ } = useSwitch(props);
  const [checked, setChecked] = useState(true);

  const handleChange = event => {
    setChecked(event.target.checked);
    checked ? greenTheme() : orangeTheme();
    console.log(checked);
    /*     console.log(checked, disabled, focusVisible); */
    //  const [checked, setChecked] = useState(true);
  };

  //   return (
  //     <>
  //       <Switch
  //         checked={checked}
  //         onChange={handleChange}
  //         inputProps={{ 'aria-label': 'controlled' }}
  //       />
  //     </>
  //   );
  // };
  const stateClasses = {
    checked,
    /*     disabled,
    focusVisible, */
  };

  return (
    <div>
      {/*       <Switch
        checked={checked}
        onChange={handleChange}
        inputProps={{ 'aria-label': 'controlled' }}
        defaultChecked
      /> */}
      <SwitchRoot className={clsx(stateClasses)}>
        <SwitchTrack>
          <SwitchThumb className={clsx(stateClasses)} />
        </SwitchTrack>
        <SwitchInput
          {...getInputProps()}
          onChange={handleChange}
          aria-label="Demo switch"
          defaultChecked
        />
      </SwitchRoot>
    </div>
  );
}

